INSERT INTO `introduceinfo` VALUES ('1', '实验室简介', '1', '', '<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">自</span><span style=\"font-size:16px;line-height:150%\">2007</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年底教育部批准本实验室筹建至今，在实验室学术委员会的指导下，在实验室建设管理委员会的大力支持和实验室建设小组的努力工作下，实验室建设已经取得一定成效，在场地规划与建设、系统平台建设以及队伍建设等方面均取得了良好的成就，</span><span style=\"font-size:16px;line-height:150%\">2011</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">月顺利通过了教育部组织的验收。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">管理学院的科研工作场所分布在</span><span style=\"font-size:16px;line-height:150%\">5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">个场所，即管理一号楼（约</span><span style=\"font-size:16px;line-height:150%\">5000</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">平方米）、管理二号楼（约</span><span style=\"font-size:16px;line-height:150%\">4000</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">平方米）、管理三号楼（约</span><span style=\"font-size:16px;line-height:150%\">600</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">平方米）、科技楼八楼（</span><span style=\"font-size:16px;line-height:150%\">1000</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">多平方米）、图书馆七楼（</span><span style=\"font-size:16px;line-height:150%\">200</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">平方米）。实验室功能布局如下：</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）管理一号楼。综合办公室（四楼）、软硬件数据中心（四楼）、物流与供应链实验室（四楼、五楼）、</span><span style=\"font-size:16px;line-height:150%\">ERP</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">实验室（四楼）、行为学实验室（四楼）；系统科学与智能决策研究所（五楼）、电子商务研究所（五楼）。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）管理二号楼。信息系统研究所（五楼）、商务智能研究所（四楼）、物流与供应链管理研究所（二楼）。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）管理三号楼。企业建模、优化与仿真研究所（三楼）、数据分析中心（二楼）。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）科技楼八楼。系统科学与智能决策研究所、工程管理研究所。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）图书馆七楼。飞行器网络系统研究所。</span>\r\n</p>\r\n<p>\r\n    &nbsp;&nbsp;<span style=\"font-size:16px;font-family:宋体\">学校针对管理学院学科发展与科研基地建设的需要，</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2013</span><span style=\"font-size:16px;font-family:宋体\">年立项建设管理学科楼，总面积</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">36000</span><span style=\"font-size:16px;font-family: 宋体\">平方米，已获得教育部的批准。预期在</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2016</span><span style=\"font-size:16px;font-family:宋体\">年学科楼建成后，整个实验室将集中到管理学科楼之中</span>\r\n</p>', '', '', '', '', '2015-08-28 00:19:00');
INSERT INTO `introduceinfo` VALUES ('2', '队伍概况', '2', '', '<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">高素质、高水平的人才队伍是实验室建设的关键，杰出带头人是关键的关键。实验室经过</span><span style=\"font-size:16px;line-height:150%\">5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年左右的建设，队伍总规模达到并稳定在</span><span style=\"font-size:16px;line-height:150%\">80</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人左右，逐渐形成一支年龄、知识、职称、学缘和学历结构更加合理，梯队层次明显、治学严谨、具有创新和发展潜力的研究队伍，争取培养出杰出学术带头人</span><span style=\"font-size:16px;line-height:150%\">5-8</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">名，学术骨干</span><span style=\"font-size:16px;line-height:150%\">15-24</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">名，努力形成国际化的国家级创新群体。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">实验室通过引进与培养相结合的方法，已经形成了由中国工程院院士为带头人的创新能力强、合作精神好的学术团队（表</span><span style=\"font-size:16px;line-height:150%\">9</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）。实验室现有</span><span style=\"font-size:16px;line-height:150%\">79</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，其中教授</span><span style=\"font-size:16px;line-height:150%\">(</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">研究员</span><span style=\"font-size:16px;line-height:150%\">)30</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人、副教授（副研究员）</span><span style=\"font-size:16px;line-height:150%\">33</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人、讲师</span><span style=\"font-size:16px;line-height:150%\">17</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人；博士生导师</span><span style=\"font-size:16px;line-height:150%\">16</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人。中国工程院院士</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，教育部长江学者</span><span style=\"font-size:16px;line-height:150%\">3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，国家外专局高端外专教授</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，</span><span style=\"font-size:16px;line-height:150%\">“</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">新世纪百千万人才工程</span><span style=\"font-size:16px;line-height:150%\">”</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">国家级人选</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，列入教育部新世纪优秀人才计划</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，黄山学者</span><span style=\"font-size:16px;line-height:150%\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，安徽省高校学术拔尖人才</span><span style=\"font-size:16px;line-height:150%\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，安徽省高校学科带头人</span><span style=\"font-size:16px;line-height:150%\">3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，安徽省高校中青年骨干教师</span><span style=\"font-size:16px;line-height:150%\">5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人。</span><span style=\"font-size:16px;line-height:150%\">2010</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年，本学科点</span><span style=\"font-size:16px;line-height:150%\">“</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">决策科学与信息系统技术</span><span style=\"font-size:16px;line-height:150%\">”</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教育部创新团队顺利通过验收。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">团队采取</span><span style=\"font-size:16px;line-height:150%\">“</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">分组</span><span style=\"font-size:16px;line-height:150%\">+</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">柔性</span><span style=\"font-size:16px;line-height:150%\">”</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">的管理模式。以学术骨干为负责人，分别建立了系统科学与智能决策、工程管理、电子商务与商务智能、企业建模优化与仿真、信息系统、物流与供应链管理和飞行器网络系统等研究所。研究所的主要任务有：</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）积极争取并组织完成相关科研课题；</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）指导培养青年教师；</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）共同指导研究生。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">对于本学科的重大课题，由团队根据研究工作的需要，进行统一规划与组织。实践表明，这是一种较好的团队组织模式。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">团队注重科研文化建设。学术团队成员在共同心愿、共同理想和共同目标下走到一起，以推动系统科学、决策科学和信息系统技术的发展为目标，相互协作、相互学习、相互提高，形成了</span><span style=\"font-size:16px;line-height:150%\">“</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">我们拥有共同的事业</span><span style=\"font-size:16px;line-height:150%\">”</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">的团队文化。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">在科研工作中，我们深刻认识到：</span><span style=\"font-size:16px;line-height:150%\">“</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">任何一项对社会发展有意义的工作，都是发展的机会，认真做好每一项这样的工作，就是抓住了一次机会，否则就失去了一次机会</span><span style=\"font-size:16px;line-height:150%\">”</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">。团队中的每个成员都积极努力的工作，在每一个研究项目中，都认真提炼其中的科学问题，发现其中的</span><span style=\"font-size:16px;line-height:150%\">“</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">机会</span><span style=\"font-size:16px;line-height:150%\">”</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">，并脚踏实地开展研究，不断开拓团队的研究思路，不断凝炼研究方向，形成了团队特有的文化，正是这种科研文化熏陶下实验室的学术团队得到持续高效发展。</span>\r\n</p>\r\n<p style=\"text-indent: 32px; line-height: 150%;\">\r\n    <br/>\r\n</p>', '', '', '', '', '2015-08-07 00:19:00');
INSERT INTO `introduceinfo` VALUES ('3', '人才培养概况', '3', '', '<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\"></span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">人才培养具体措施有：</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）创造机会让实验室成员到国内外学习、做访问学者、博士后研究工作或攻读博士学位。三年来，实验室共派出</span><span style=\"font-size:16px;line-height:150%\">22</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">名学者、青年教师前往美国、日本、加拿大、英国、法国、香港等国家、地区进修或开展合作研究；派出</span><span style=\"font-size:16px;line-height:150%\">4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">名青年教师到国内著名高校访问学习；</span><span style=\"font-size:16px;line-height:150%\">19</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人获得博士学位。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）积极引进国内外优秀的中青年专家学者和高水平大学的博士毕业生。三年来，加入实验室研究队伍的海外学者</span><span style=\"font-size:16px;line-height:150%\">8</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，引进百篇优秀博士论文获得者</span><span style=\"font-size:16px;line-height:150%\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人，新进国内外著名高校毕业的博士</span><span style=\"font-size:16px;line-height:150%\">18</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">人。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">（</span><span style=\"font-size:16px;line-height:150%\">3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">）鼓励青年教师开展科研工作，提高教师的理论水平和实际动手能力；鼓励教师参加国内外学术活动，包括参加国外国际会议、国内国际国内学术会议，以及国内教育部相关专业建设指导委员会组织的各种教学研究会议。</span>', '', '', '', '', '2015-08-12 00:19:00');
INSERT INTO `introduceinfo` VALUES ('4', '科学研究概况', '4', '', '<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\"></span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\"></span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family: 宋体\">实验室从</span><span style=\"font-size:16px;line-height:150%\">2010</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年至今，坚持在管理实践中发现科学问题，在攻克科学技术难关过程中不断提高水平，在建设先进科研文化过程中和谐快速发展。在系统优化理论与方法、智能决策理论与方法、信息管理系统工程等研究方向上形成了较强的优势和明显的特色。在科学研究、人才培养和社会服务等方面均取得了有较高显示度的成果，为经济建设和社会发展做出了应有的贡献。</span>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family:宋体\"></span><br/>\r\n</p>\r\n<p style=\"text-indent:32px;line-height:150%\">\r\n    <span style=\"font-size:16px;line-height:150%;font-family:宋体\"></span><br/>\r\n</p>\r\n<p style=\"text-indent: 32px; line-height: 150%;\">\r\n    <br/>\r\n</p>', '', '', '', '', '2015-08-28 15:35:00');
