INSERT INTO `organization` VALUES ('4', '企业建模与优化研究所', '2015-09-07 20:39:00', '<p>\r\n    <strong><span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 25.2000007629395px; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 25.2000007629395px; background-color: rgb(255, 255, 255);\"><span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 25.2000007629395px; background-color: rgb(255, 255, 255);\"><span style=\"font-family: Tahoma, Helvetica, SimSun, sans-serif; font-size: 14px; line-height: 25.2000007629395px; background-color: rgb(255, 255, 255);\">企业建模与优化研究所成立于2005年，由长江学者讲座教授、法国国家一级教授、法国特鲁瓦工业大学教授、我院特聘教授储诚斌博士任首任所长。主要围绕管理信息化、生产过程优化、MRO和再制造等领域的信息技术与决策、预测、优化问题进行研究。该所利用与法国特鲁瓦工业大学的合作关系，把研究与国外先进研究机构同步。该研究团队现有人员8名，有博士学位人员5人。</span></span></span></span></strong><br/>\r\n</p>');
